package inspi.java;

public class ModOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=11;
		
		System.out.println(i%2);// this prints the reminder...
		
		i+=10;
		//this is equivalent to i=i+10;
		
		System.out.println(i);
		
		i*=10;
		System.out.println(i);
		//homework - do for -=,/= and %=
		
	}

}
