package inspi.java;

public class VowelAphabate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'i';
		if(ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u' )
		System.out.println(ch + " is vowel");
		else
		System.out.println(ch + " is consonant");
	}

}
